import Event from "../../base/Events";
import ajaxRequest from "../../ajax/AjaxRequest";
import cacheManager from "../CacheManager";

export default class extends Event {

    constructor(url, name) {
        super();
        this._url = url;
        this._name = name;
        cacheManager.add(this);
    }

    /**
     * 初始化缓存
     */
    initCache() {
        this.fetchData().then(function (data) {
            this.cacheData(data);
        }.bind(this))
    }

    /**
     * 获取数据
     * @returns {*}
     */
    fetchData() {
        return new Promise((resolve) => {
            let cacheData = store.get(this.name);
            if (cacheData) {
                resolve(cacheData);
            } else {
                if (this.url) {
                    ajaxRequest.ajax({url: this.url, type: "get"}).then(function (data) {
                        resolve(data);
                        store.set(this.name, data)
                    }.bind(this))
                }else{
                    console.error(`没有设置[${this.name}] 缓存处理器的请求URL，此缓存无效！`)
                }

            }
        });
    }

    /**
     * 缓存数据处理
     * @param data
     */
    cacheData(data) {
        throw new Error("请在BaseCache子类中实现cacheData方法以便进行缓存处理！");
    }

    get url() {
        return this._url;
    }

    set url(value) {
        this._url = value;
    }

    get name() {
        return this._name;
    }

    set name(value) {
        this._name = value;
    }

};
