let currentTarget = null;
export default {
    /**
     * 设置当前事件源
     * @param currentEventTarget
     */
    setCurrentEventTarget: function (currentEventTarget) {
        currentTarget = currentEventTarget;
    },

    /**
     * 获取当前事件源
     * @returns {*}
     */
    getCurrentEventTarget: function () {
        return currentTarget;
    }

};