import Application from "../core/Application";
import Layout from "./showcase/layout/Layout";

import "./moduleImport";

const application = new Application({
    menuUrl: 'authz/security/menu/list/QYXJRXXDZHCKXT_1702222250',
    dictionaryUrl:"crud/test/dictionary",
    layout: new Layout()
});
application.addEvent("initialized", function () {
    /* 屏蔽回车 不屏蔽textarea 换行 */
    $(document).keydown(function (event) {
        if (event.keyCode === 13) {
            if (!$(event.target).is("textarea")) {
                return false;
            }
        }
    });
});

application.initApplication();

