import handlerManager from "../core/handler/HandlerManager";

import dictionary from "../system/dictionary/Handler";
dictionary.ajaxBasePath = "/crud";
handlerManager.staticImport({
    "system/dictionary": dictionary
});

handlerManager.dynamicImport(function (path) {
    try {
        return import(`./${path}/Handler.js`);
    } catch (err) {
        console.error("load handler error");
    }
});