import Handler from "../../../core/handler/Handler";

export default new Handler({
    resolveTpl: function (tpl) {
        return require("./tpl/" + tpl + ".html");
    },
    onIndexed:function () {
        console.info('crud/index.html页加载完成');
    }
});

