import Events from "../../core/base/Events";

let event=new Events();
event.addEvent("added",function (data) {
    alert("edded:"+data.name);
});


let edit=function (data) {
    alert("edit:"+data.name);
};
event.addEvents({edit:edit});

event.fireEvent("added",{name:"zhao"});
event.fireEvent("edit",{name:"zhao"},3000);

event.removeEvent("edit",edit);
event.fireEvent("edit",{name:"zhao"});

